<?php

/**
 * FacturasTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FacturasTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FacturasTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Facturas');
    }
    
    public function retrieveBackendFacturasList(Doctrine_Query $q)
    {
      $rootAlias = $q->getRootAlias();
      $q->leftJoin($rootAlias . '.Cotizaciones cot');
      $q->leftJoin('cot.Clientes cli');
      $user=  sfContext::getInstance()->getUser()->getGuardUser();
      if(!$user->getIsSuperAdmin()){
            //evitar que todos vean todo. solo el super usuario.
            $q->andWhere('cot.user_id=?',$user->getId());    
      }
      return $q;
    }
    public function getFacturaConContizacionesClientesForId($id){
      $q=$this->createQuery('f');  
      $rootAlias = $q->getRootAlias();
      $q->leftJoin($rootAlias . '.Cotizaciones cot');
      $q->leftJoin('cot.Clientes cli');
      $q->addWhere($rootAlias.'.id=?',$id);
      return $q->fetchOne();
    }
    
    public function getCriteriaUltimasFacturas($activos=true){
        $q=$this->createQuery('f');  
        $rootAlias = $q->getRootAlias();
        $q->leftJoin($rootAlias . '.Cotizaciones cot');
        $q->leftJoin('cot.Clientes cli');
        $q->leftJoin('cot.Contactos co');
        $q->andWhere($rootAlias.'.is_active=?',$activos);
        $user=  sfContext::getInstance()->getUser()->getGuardUser();
        if(!$user->getIsSuperAdmin()){
            //evitar que todos vean todo. solo el super usuario.
            $q->andWhere('cot.user_id=?',$user->getId());    
        }
        
        return $q;
    }
    public function getUltimasFacturas($activos=true){
        $q=$this->getCriteriaUltimasFacturas($activos);
        return $q->execute();
    }
    
}